import React from 'react';
import logo from './logo.svg';
import './App.css';

class App extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      started: false,
      pause: false,
      time: 0
    }
  }



  render () {
    const pause = this.state.pause
    let status
    if (pause) {
      status = "Pause"
    } else {
      status = "Travail"
    }

    return (
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <h4>{status}</h4>
        </header>
      </div>
    );
  }
}

export default App;
