import React from 'react';
import logo from './logo.svg';
import './App.css';

class App extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      started: false,
      pause: false,
      start: 0,
      timer: 0
    }
  }

  startTimer = () => {
    this.setState({
      started: true,
      timer: this.state.timer,
      start: Date.now() - this.state.timer
    });
    this.timer = setInterval(() => {
      this.setState({
        timer: Date.now() - this.state.start
      });
    }, 10);
  };

  render () {
    const pause = this.state.pause
    let status
    if (pause) {
      status = "Pause"
    } else {
      status = "Travail"
    }

    return (
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <h4>{status}</h4>

          <button onClick={this.startTimer}></button>
        </header>
      </div>
    );
  }
}

export default App;
