import React from 'react';
import logo from './logo.svg';
import './App.css';

class App extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      started: false,
      pause: false,
      timer: 0,
      pauseTime: 300000,
      workTime: 100000
    }
  }

  startTimer = () => {
    this.setState({
      started: true,
      timer: (this.state.pauseTime + this.state.workTime) - this.state.timer,
    });
    this.timer = setInterval(() => {
      this.setState({ timer: this.state.timer - 100 });
      if (this.state.timer < this.state.pauseTime) {
        this.setState({ pause: true })
      } else {
        this.setState({ pause: false })
      }
      if (this.state.timer < 0) {
        this.setState({ timer: this.state.pauseTime + this.state.workTime })
        alert("Work done !")
      }
    }, 10);
  };

  stopTimer = () => {
    this.setState({ started: false })
    clearInterval(this.timer);
  };

  resetTimer = () => {
    this.stopTimer()
    this.setState({ timer: this.state.pauseTime + this.state.workTime })
  };

  changeWorkTime = (event) => {
    this.setState({ workTime: parseInt(event.target.value)})
  }

  changePauseTime = (event) =>  {
    this.setState({ pauseTime: event.target.value})
  }

  render () {
    const pause = this.state.pause
    const pauseTime = this.state.pauseTime
    const started = this.state.started
    const { timer } = this.state
    let seconds = ("0" + (Math.floor(timer / 1000) % 60)).slice(-2)
    let minutes = ("0" + (Math.floor(timer / 60000) % 60)).slice(-2); 
    let status
    if (pause) {
      status = "Break time"
    } else if (!pause && started) {
      status = "Work time"
    } else {
      status = "Stopped"
    }

    let compteur;
    if (timer <= (pauseTime+20000) && !pause || timer <= 20000 && pause) {
      compteur = <h5 class="red-text">{minutes}m {seconds}s</h5>
    } else {
      compteur = <h5>{minutes}m {seconds}s</h5>
    }

    return (
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <h4>{status}</h4>
          <input type="number" value={this.state.pauseTime} onChange={this.changePauseTime} />
          <input type="number" value={this.state.workTime} onChange={this.changeWorkTime} />
          {compteur}
          <button onClick={this.startTimer}>Start</button>
          <button onClick={this.stopTimer}>Stop</button>
          <button onClick={this.resetTimer}>Reset</button>
        </header>
      </div>
    );
  }
}

export default App;
