import React from 'react';
import logo from './logo.svg';
import './App.css';

class App extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      started: false,
      pause: false,
      timer: 0,
      pauseTime: 300000,
      workTime: 300000
    }
  }

  startTimer = () => {
    if (this.state.timer == 0) {
      this.setState({
        started: true,
        timer: this.state.pauseTime + this.state.workTime,
      });
    } else {
      this.setState({
        started: true,
      });
    }    
    this.timer = setInterval(() => {
      this.setState({ timer: this.state.timer - 100 });
      if (this.state.timer < this.state.pauseTime) {
        this.setState({ pause: true })
      } else {
        this.setState({ pause: false })
      }
      if (this.state.timer == 0 && this.state.started) {
        this.resetTimer();
        alert("Work done !")
      }
    }, 10);
  };

  stopTimer = () => {
    this.setState({ started: false })
    clearInterval(this.timer);
  };

  resetTimer = () => {
    this.stopTimer()
    this.setState({ 
      timer: this.state.pauseTime + this.state.workTime,
      pause: false,
      started: false
    })
  };

  changeWorkTime = (event) => {
    this.setState({ workTime: parseInt(event.target.value)*60000})
    this.resetTimer();
  }

  changePauseTime = (event) =>  {
    this.setState({ pauseTime: parseInt(event.target.value)*60000})
    this.resetTimer();
  }

  render () {
    const pause = this.state.pause
    const pauseTime = this.state.pauseTime
    const started = this.state.started
    const { timer } = this.state
    let seconds = ("0" + (Math.floor(timer / 1000) % 60)).slice(-2)
    let minutes = ("0" + (Math.floor(timer / 60000) % 60)).slice(-2); 
    let status
    if (pause) {
      status = "Pause time"
    } else if (!pause && started) {
      status = "Work time"
    } else {
      status = "Stopped"
    }

    let compteur;
    if (timer <= (pauseTime+20000) && !pause || timer <= 20000 && pause) {
      compteur = <h5 class="red-text">{minutes}m {seconds}s</h5>
    } else {
      compteur = <h5>{minutes}m {seconds}s</h5>
    }

    return (
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <h4>{status}</h4>
          <label>
            Working time: (minutes)
            <input type="number" value={this.state.workTime/60000} onChange={this.changeWorkTime} />
          </label>
          <label>
            Pause time: (minutes)
            <input type="number" value={this.state.pauseTime/60000} onChange={this.changePauseTime} />  
          </label>
          {compteur}
          <button onClick={this.startTimer}>Start</button>
          <button onClick={this.stopTimer}>Stop</button>
          <button onClick={this.resetTimer}>Reset</button>
        </header>
      </div>
    );
  }
}

export default App;
