import React from 'react';
import logo from './logo.svg';
import './App.css';

class App extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      started: false,
      pause: false,
      timer: 0,
      pauseTime: 300000,
      wokTime: 1800000
    }
  }

  startTimer = () => {
    this.setState({
      started: true,
      timer: this.state.pauseTime + this.state.wokTime - this.state.timer,
    });
    this.timer = setInterval(() => {
      this.setState({
        timer: this.state.timer - 1000
      });
      if (this.state.timer < this.state.pauseTime) {
        this.setState({ pause: true })
      } else {
        this.setState({ pause: false })
      }
      if (this.state.timer == 0) {
        this.setState({ timer: 1800000 })
        alert("Work done !")
      }
    }, 10);
  };

  stopTimer = () => {
    this.setState({ started: false })
    clearInterval(this.timer);
  };

  resetTimer = () => {
    this.stopTimer()
    this.setState({ timer: 1800000 })
  };

  render () {
    const pause = this.state.pause
    const pauseTime = this.state.pauseTime
    const started = this.state.started
    const { timer } = this.state
    let seconds = ("0" + (Math.floor((timer-pauseTime) / 1000) % 60)).slice(-2)
    let minutes = ("0" + (Math.floor((timer-pauseTime) / 60000) % 60)).slice(-2); 
    let status
    if (pause) {
      status = "Break time"
      seconds = ("0" + (Math.floor(timer / 1000) % 60)).slice(-2)
      minutes = ("0" + (Math.floor(timer / 60000) % 60)).slice(-2)
    } else if (!pause && started) {
      status = "Work time"
    } else {
      status = "Stopped"
    }

    //TODO
    //if timer <= 320000 && !pause change color to red

    return (
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <h4>{status}</h4>
          {minutes}m {seconds}s
          <button onClick={this.startTimer}>Start</button>
          <button onClick={this.stopTimer}>Stop</button>
          <button onClick={this.resetTimer}>Reset</button>
        </header>
      </div>
    );
  }
}

export default App;
